import { ALL_EXTENSIONS } from '@gltf-transform/extensions';
import { ALL_EXTENSIONS as NEEDLE_EXTENSIONS } from "./dist/extensions/NEEDLE_opaque/index.js";
import { NEEDLE_progressive_texture } from './dist/extensions/NEEDLE_progressive_texture/index.js';


export function registerExtensions(node) {
    node.registerExtensions(ALL_EXTENSIONS);
    node.registerExtensions(NEEDLE_EXTENSIONS);
    node.registerExtensions([
        NEEDLE_progressive_texture
    ])
}